import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';

export default function BookmarkButton({ project }) {
  const { account } = useAuth();
  const userId = account?.sub;
  const [bookmarked, setBookmarked] = useState(false);

  useEffect(() => {
    // if this project already has a bookmark_id field, mark it bookmarked
    setBookmarked(!!project.bookmark_id);
  }, [project]);

  const toggle = async () => {
    if (!userId) return;
    if (!bookmarked) {
      await api.addBookmark(userId, project);
      setBookmarked(true);
    } else {
      await api.removeBookmark(userId, project.id);
      setBookmarked(false);
    }
  };

  return (
    <button onClick={toggle}>
      {bookmarked ? 'üíî Unbookmark' : '‚≠ê Bookmark'}
    </button>
  );
}
