import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../Styles/Navbar.css';

export default function Navbar() {
  const { isAuthenticated, login, logout, user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 20;
      setScrolled(isScrolled);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu on route change
  useEffect(() => {
    setIsMenuOpen(false);
  }, [location]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const isActiveLink = (path) => {
    return location.pathname === path;
  };

  const handleProfileClick = () => {
    navigate('/profile');
    setIsMenuOpen(false);
  };

  const handleLogout = () => {
    logout();
    setIsMenuOpen(false);
  };

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`} aria-label="Main navigation">
      <div className="navbar-container">
        {/* Logo/Brand */}
        <Link to="/" className="navbar-brand">
          <div className="brand-icon">🔍</div>
          <div className="brand-text">
            <span className="brand-name">OSProject</span>
            <span className="brand-subtitle">Finder</span>
          </div>
        </Link>

        {/* Desktop Navigation Links */}
        <div className="navbar-nav">
          <Link 
            to="/" 
            className={`nav-link ${isActiveLink('/') ? 'active' : ''}`}
            aria-current={isActiveLink('/') ? 'page' : undefined}
          >
            <span className="nav-icon">🏠</span>
            <span className="nav-text">Home</span>
          </Link>

          <Link 
            to="/search" 
            className={`nav-link ${isActiveLink('/search') ? 'active' : ''}`}
            aria-current={isActiveLink('/search') ? 'page' : undefined}
          >
            <span className="nav-icon">🔍</span>
            <span className="nav-text">Search</span>
          </Link>

          {isAuthenticated && (
            <>
              <Link 
                to="/recommend" 
                className={`nav-link ${isActiveLink('/recommend') ? 'active' : ''}`}
                aria-current={isActiveLink('/recommend') ? 'page' : undefined}
              >
                <span className="nav-icon">✨</span>
                <span className="nav-text">Recommended</span>
                <span className="nav-badge">AI</span>
              </Link>

              <Link 
                to="/bookmarks" 
                className={`nav-link ${isActiveLink('/bookmarks') ? 'active' : ''}`}
                aria-current={isActiveLink('/bookmarks') ? 'page' : undefined}
              >
                <span className="nav-icon">📚</span>
                <span className="nav-text">Bookmarks</span>
              </Link>
            </>
          )}
        </div>

        {/* User Section */}
        <div className="navbar-user">
          {!isAuthenticated ? (
            <button 
              onClick={login} 
              className="login-button"
              aria-label="Login with GitHub"
            >
              <span className="button-icon">🚀</span>
              <span className="button-text">Sign In</span>
            </button>
          ) : (
            <div className="user-menu">
              <button 
                className="user-profile-button"
                onClick={handleProfileClick}
                aria-label={`Profile menu for ${user?.name || 'User'}`}
              >
                <img 
                  src={user?.picture || `https://ui-avatars.com/api/?name=${encodeURIComponent(user?.name || 'User')}&background=667eea&color=fff`}
                  alt="Profile"
                  className="user-avatar"
                />
                <div className="user-info">
                  <span className="user-name">{user?.name || 'User'}</span>
                  <span className="user-email">{user?.email || ''}</span>
                </div>
                <span className="dropdown-arrow">▼</span>
              </button>

              <div className="user-dropdown">
                <button 
                  onClick={handleProfileClick}
                  className="dropdown-item"
                >
                  <span className="item-icon">👤</span>
                  Profile
                </button>
                <button 
                  onClick={() => navigate('/bookmarks')}
                  className="dropdown-item"
                >
                  <span className="item-icon">📚</span>
                  My Bookmarks
                </button>
                <div className="dropdown-divider"></div>
                <button 
                  onClick={handleLogout}
                  className="dropdown-item logout-item"
                >
                  <span className="item-icon">🚪</span>
                  Sign Out
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Mobile Menu Button */}
        <button 
          className={`mobile-menu-button ${isMenuOpen ? 'active' : ''}`}
          onClick={toggleMenu}
          aria-label="Toggle navigation menu"
          aria-expanded={isMenuOpen}
        >
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
          <span className="hamburger-line"></span>
        </button>
      </div>

      {/* Mobile Menu */}
      <div className={`mobile-menu ${isMenuOpen ? 'open' : ''}`}>
        <div className="mobile-menu-content">
          <Link 
            to="/" 
            className={`mobile-nav-link ${isActiveLink('/') ? 'active' : ''}`}
            onClick={() => setIsMenuOpen(false)}
          >
            <span className="nav-icon">🏠</span>
            Home
          </Link>

          <Link 
            to="/search" 
            className={`mobile-nav-link ${isActiveLink('/search') ? 'active' : ''}`}
            onClick={() => setIsMenuOpen(false)}
          >
            <span className="nav-icon">🔍</span>
            Search
          </Link>

          {isAuthenticated && (
            <>
              <Link 
                to="/recommend" 
                className={`mobile-nav-link ${isActiveLink('/recommend') ? 'active' : ''}`}
                onClick={() => setIsMenuOpen(false)}
              >
                <span className="nav-icon">✨</span>
                Recommended
                <span className="nav-badge">AI</span>
              </Link>

              <Link 
                to="/bookmarks" 
                className={`mobile-nav-link ${isActiveLink('/bookmarks') ? 'active' : ''}`}
                onClick={() => setIsMenuOpen(false)}
              >
                <span className="nav-icon">📚</span>
                Bookmarks
              </Link>

              <div className="mobile-divider"></div>

              <button 
                onClick={handleProfileClick}
                className="mobile-nav-link"
              >
                <span className="nav-icon">👤</span>
                Profile
              </button>
            </>
          )}

          <div className="mobile-auth-section">
            {!isAuthenticated ? (
              <button 
                onClick={() => { login(); setIsMenuOpen(false); }} 
                className="mobile-login-button"
              >
                <span className="button-icon">🚀</span>
                Sign In with GitHub
              </button>
            ) : (
              <button 
                onClick={handleLogout}
                className="mobile-logout-button"
              >
                <span className="button-icon">🚪</span>
                Sign Out
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Mobile Menu Overlay */}
      {isMenuOpen && (
        <div 
          className="mobile-menu-overlay" 
          onClick={() => setIsMenuOpen(false)}
          aria-hidden="true"
        ></div>
      )}
    </nav>
  );
}
