import axios from 'axios';

const client = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000',
  timeout: 10000,
  withCredentials: false,
});

const api = {
  // Projects
  getProjects: (params) => client.get('/api/projects', { params }),
  getProject: (id) => client.get(`/api/projects/${id}`),

  // Bookmarks
  getBookmarks: () => client.get('/api/bookmarks'),
  addBookmark: (projectId) =>
    client.post('/api/bookmarks', { project_id: projectId }),
  removeBookmark: (bookmarkId) =>
    client.delete(`/api/bookmarks/${bookmarkId}`),

  // GitHub sync
  syncGitHub: () => client.post('/api/github/sync'),

  // Auth (if used)
  login: () => client.post('/api/auth/login'),
  logout: () => client.post('/api/auth/logout'),
  getProfile: () => client.get('/api/auth/profile'),
};

export default api;
