// src/context/AuthContext.js
import React, { createContext, useContext, useEffect, useState } from "react";
import { msalInstance, getToken } from "../services/auth";

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [account, setAccount] = useState(null);
  const [idToken, setIdToken] = useState(null);

  useEffect(() => {
    const accts = msalInstance.getAllAccounts();
    if (accts.length > 0) {
      setAccount(accts[0]);
      getToken().then(setIdToken);
    }
  }, []);

  const login = async () => {
    try {
      const loginResult = await msalInstance.loginPopup({
        scopes: ["openid", "profile", "email"],
      });
      setAccount(loginResult.account);
      const token = await getToken();
      setIdToken(token);
    } catch (e) {
      console.error("Login error:", e);
    }
  };

  const logout = () => {
    msalInstance.logoutPopup();
    setAccount(null);
    setIdToken(null);
  };

  return (
    <AuthContext.Provider value={{ account, idToken, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
