import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../Styles/SearchPage.css';

const SearchPage = () => {
  const [query, setQuery] = useState('');
  const [language, setLanguage] = useState('');
  const [topic, setTopic] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      if (query.trim() || language.trim() || topic.trim()) {
        setLoading(true);
        setError('');
        axios.get(`${process.env.REACT_APP_API_URL}/api/projects`, {
          params: { q: query, language, topic }
        })
          .then(res => setResults(Array.isArray(res.data) ? res.data : []))
          .catch(() => {
            setError('Failed to fetch projects.');
            setResults([]); // Always set to array on error
          })
          .finally(() => setLoading(false));
      } else {
        setResults([]);
      }
    }, 400);

    return () => clearTimeout(delayDebounce);
  }, [query, language, topic]);

  return (
    <div className="search-container">
      <h2 className="search-title">Search Projects</h2>
      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Search by name or description"
          className="search-input"
        />
        <input
          type="text"
          value={language}
          onChange={e => setLanguage(e.target.value)}
          placeholder="Language (e.g. Go)"
          className="search-input"
        />
        <input
          type="text"
          value={topic}
          onChange={e => setTopic(e.target.value)}
          placeholder="Topic (e.g. cloud)"
          className="search-input"
        />
      </div>
      {loading && <div>Loading...</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <ul>
        {(Array.isArray(results) ? results : []).map(proj => (
          <li key={proj.id || proj.url} className="search-result">
            <a href={proj.url} target="_blank" rel="noopener noreferrer" className="search-result-link">
              {proj.name}
            </a>
            <p>{proj.description}</p>
            <div>
              <strong>Languages:</strong> {Array.isArray(proj.languages) ? proj.languages.join(', ') : proj.languages}
              {proj.topics && (
                <>
                  <br />
                  <strong>Topics:</strong> {Array.isArray(proj.topics) ? proj.topics.join(', ') : proj.topics}
                </>
              )}
            </div>
            <div>
              <span>⭐ {proj.stars || proj.stargazers_count || 0}</span>
              <span style={{ marginLeft: 10 }}>Last updated: {proj.last_updated || proj.updated_at}</span>
            </div>
          </li>
        ))}
      </ul>
      {!loading && Array.isArray(results) && results.length === 0 && (query || language || topic) && (
        <div>No projects found.</div>
      )}
    </div>
  );
};

export default SearchPage;
