// src/pages/SearchPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../Styles/SearchPage.css';

const SearchPage = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      if (query.trim()) {
        axios.get(`${process.env.REACT_APP_API_URL}/api/projects`, {
          params: { q: query }
        }).then(res => setResults(res.data))
          .catch(console.error);
      } else {
        setResults([]);
      }
    }, 500); // debounce time

    return () => clearTimeout(delayDebounce);
  }, [query]);

  return (
    <div className="search-container">
      <h2 className="search-title">Search Projects</h2>
      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="e.g. kubernetes"
          className="search-input"
        />
      </div>
      <ul>
        {results.map(proj => (
          <li key={proj.id} className="search-result">
            <a href={proj.url} target="_blank" rel="noopener noreferrer" className="search-result-link">
              {proj.name}
            </a>
            <p>{proj.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchPage;
