// src/components/Navbar.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "../Styles/Navbar.css";

export default function Navbar() {
  const { isAuthenticated, login, logout, account } = useAuth();
  const [showProfile, setShowProfile] = useState(false);

  const toggleProfile = () => setShowProfile((prev) => !prev);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-links">
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/search" className="navbar-link">Search</Link>

          {isAuthenticated && (
            <>
              <Link to="/profile" className="navbar-link">Profile</Link>
              <Link to="/bookmarks" className="navbar-link">Bookmarks</Link>
            </>
          )}
        </div>

        <div className="navbar-right">
          {!isAuthenticated ? (
            <button onClick={login} className="navbar-button">Login</button>
          ) : (
            <>
              <button onClick={toggleProfile} className="navbar-button">
                Hi, {account?.name?.toUpperCase()}
              </button>
              <button onClick={logout} className="navbar-button">Logout</button>
            </>
          )}
        </div>
      </nav>

      {/* Profile Dropdown if authenticated */}
      {isAuthenticated && showProfile && account && (
        <div className="profile-dropdown">
          {account.picture && (
            <img
              src={account.picture}
              alt="Profile"
              className="profile-avatar-dropdown"
            />
          )}
          <p><strong>Name:</strong> {account.name}</p>
          <p><strong>Email:</strong> {account.email}</p>
          <p><strong>Provider:</strong> {account.sub?.split("|")[0]}</p>
        </div>
      )}
    </>
  );
}
