import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import ProjectCard from '../components/ProjectCard';

export default function ProfilePage() {
  const { account, logout, isAuthenticated } = useAuth();
  const [starredProjects, setStarredProjects] = useState([]);

  useEffect(() => {
    if (!isAuthenticated) return;
    // Example: fetch user’s starred/open-source projects from your API
    api.getProfile() // assume /api/auth/profile returns user info
      .then(res => {
        // if your backend returns a list of starred repos:
        const list = Array.isArray(res.data.starred) ? res.data.starred : [];
        setStarredProjects(list);
      })
      .catch(() => setStarredProjects([]));
  }, [isAuthenticated]);

  if (!isAuthenticated) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>Please log in to view your profile.</h2>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Your Profile</h1>
      <p><strong>Name:</strong> {account?.name}</p>
      <p><strong>Email:</strong> {account?.email}</p>
      <button onClick={logout} style={{ marginBottom: '1rem' }}>
        Logout
      </button>

      <h2>Your Starred Projects</h2>
      {starredProjects.length === 0 ? (
        <p>You haven't starred any projects yet.</p>
      ) : (
        starredProjects.map(proj => (
          <ProjectCard key={proj.id} project={proj} />
        ))
      )}
    </div>
  );
}
