import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Navbar() {
  const { isAuthenticated, login, logout, user } = useAuth();
  const navigate = useNavigate();
  const { pathname } = useLocation();
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 10);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const navLink = (to, label, icon) => <Link to={to}>{icon} {label}</Link>;

  return (
    <nav>
      <div>
        <Link to="/">üîç OSProject Finder</Link>
        <div>
          {navLink('/', 'Home', 'üè†')} {navLink('/search', 'Search', 'üîç')}
          {isAuthenticated && (<>{navLink('/recommend', 'Recommended', '‚ú®')} {navLink('/bookmarks', 'Bookmarks', 'üìö')}</>)}
        </div>
        <div>
          {isAuthenticated ? (
            <div>
              <span>Hi, {user?.name || user?.preferred_username || 'User'}!</span>
              <button onClick={() => navigate('/profile')}>üë§ Profile</button>
              <button onClick={logout}>üö™ Logout</button>
            </div>
          ) : (
            <button onClick={login}>üöÄ Login</button>
          )}
        </div>
      </div>
    </nav>
  );
}
