import React, { useState, useEffect } from 'react';
import api from '../services/api';

export default function BookmarkButton({ project }) {
  const [bookmarked, setBookmarked] = useState(false);
  const [bmId, setBmId] = useState('');

  useEffect(() => {
    if (project.bookmark_id) {
      setBookmarked(true);
      setBmId(project.bookmark_id);
    }
  }, [project]);

  const toggleBookmark = async () => {
    if (!bookmarked) {
      const res = await api.addBookmark(project.id);
      setBookmarked(true);
      setBmId(res.data.id);
    } else {
      await api.removeBookmark(bmId);
      setBookmarked(false);
      setBmId('');
    }
  };

  return (
    <button onClick={toggleBookmark}>
      {bookmarked ? 'üíî Unbookmark' : '‚≠ê Bookmark'}
    </button>
  );
}
