import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const ProfilePage = () => {
  const { account } = useAuth();
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      const token = account.idToken; // MSAL puts idToken on account object
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/profile`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile(res.data);
    };
    if (account) fetchProfile();
  }, [account]);

  if (!profile) return <p>Loading...</p>;

  return (
    <div className="p-8">
      <h2 className="text-2xl mb-4">My Profile</h2>
      <p><strong>Name:</strong> {profile.name}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      {/* Add more fields or edit form here */}
    </div>
  );
};

export default ProfilePage;
