import React, { useState } from 'react';

export default function SyncButton() {
  const [status, setStatus] = useState<'idle'|'syncing'|'done'|'error'>('idle');
  const [msg, setMsg] = useState('');

  async function handleSync() {
    setStatus('syncing');
    setMsg('');
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/api/github/sync`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });
      const body = await res.json();
      if (res.ok) {
        setStatus('done');
        setMsg('Trending repos synced successfully.');
      } else {
        setStatus('error');
        setMsg(body.error || 'Unknown error');
      }
    } catch (err) {
      setStatus('error');
      setMsg(err.message);
    }
  }

  return (
    <div style={{ margin: '1rem 0', textAlign: 'center' }}>
      <button onClick={handleSync} disabled={status==='syncing'}>
        {status==='syncing' ? 'Syncing…' : 'Sync Trending'}
      </button>
      {status==='done'  && <p style={{ color: 'green'  }}>{msg}</p>}
      {status==='error' && <p style={{ color: 'red'    }}>{msg}</p>}
    </div>
  );
}
