import React, { useEffect, useState } from 'react';
import ProjectCard from '../components/ProjectCard';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';
import '../Styles/SearchPage.css'; // Reuse search page styles

export default function RecommendPage() {
  const { user, userId, isAuthenticated } = useAuth();
  const [allRecommendations, setAllRecommendations] = useState([]);
  const [filteredProjects, setFilteredProjects] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Filter states
  const [query, setQuery] = useState('');
  const [language, setLanguage] = useState('');
  const [topic, setTopic] = useState('');
  const [minStars, setMinStars] = useState('');
  const [sortBy, setSortBy] = useState('score'); // score, stars, updated

 // In RecommendPage.js, update the API call in useEffect:
useEffect(() => {
  if (!isAuthenticated || !user) return;

  setLoading(true);
  setError('');

  api.recommend(user, userId) // Pass userId for bookmark status
    .then(res => {
      const list = Array.isArray(res.data) ? res.data : [];
      setAllRecommendations(list);
      setFilteredProjects(list);
    })
    .catch(() => {
      setError('Failed to load recommendations');
      setAllRecommendations([]);
      setFilteredProjects([]);
    })
    .finally(() => setLoading(false));
}, [user, userId, isAuthenticated]); // Add userId to dependencies

  // Filter and sort projects
  useEffect(() => {
    let filtered = [...allRecommendations];

    // Apply text search
    if (query.trim()) {
      const searchTerm = query.toLowerCase();
      filtered = filtered.filter(proj => 
        proj.name.toLowerCase().includes(searchTerm) ||
        (proj.description && proj.description.toLowerCase().includes(searchTerm))
      );
    }

    // Apply language filter
    if (language.trim()) {
      const langFilter = language.toLowerCase();
      filtered = filtered.filter(proj =>
        proj.languages && proj.languages.some(lang => 
          lang.toLowerCase().includes(langFilter)
        )
      );
    }

    // Apply topic filter
    if (topic.trim()) {
      const topicFilter = topic.toLowerCase();
      filtered = filtered.filter(proj =>
        proj.topics && proj.topics.some(t => 
          t.toLowerCase().includes(topicFilter)
        )
      );
    }

    // Apply minimum stars filter
    if (minStars.trim() && !isNaN(minStars)) {
      const minStarsNum = parseInt(minStars);
      filtered = filtered.filter(proj => proj.stars >= minStarsNum);
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'stars':
          return b.stars - a.stars;
        case 'updated':
          return new Date(b.last_updated || b.updated_at) - new Date(a.last_updated || a.updated_at);
        case 'name':
          return a.name.localeCompare(b.name);
        case 'score':
        default:
          // If projects have scores, sort by them, otherwise by stars
          return (b.score || b.stars) - (a.score || a.stars);
      }
    });

    setFilteredProjects(filtered);
  }, [allRecommendations, query, language, topic, minStars, sortBy]);

  // Clear all filters
  const clearFilters = () => {
    setQuery('');
    setLanguage('');
    setTopic('');
    setMinStars('');
    setSortBy('score');
  };

  if (!isAuthenticated) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>Please log in to see recommendations.</h2>
      </div>
    );
  }

  if (loading) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>Loading recommendations...</h2>
      </div>
    );
  }

  return (
    <div className="search-container">
      <h2 className="search-title">Recommended Projects for You</h2>

      {/* Filters Section */}
      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Search by name or description"
          className="search-input"
        />
        <input
          type="text"
          value={language}
          onChange={e => setLanguage(e.target.value)}
          placeholder="Language (e.g. Go, Python)"
          className="search-input"
        />
        <input
          type="text"
          value={topic}
          onChange={e => setTopic(e.target.value)}
          placeholder="Topic (e.g. cloud, web)"
          className="search-input"
        />
        <input
          type="number"
          value={minStars}
          onChange={e => setMinStars(e.target.value)}
          placeholder="Min Stars"
          className="search-input"
          min="0"
        />
      </div>

      {/* Sort and Clear Controls */}
      <div style={{ 
        display: 'flex', 
        gap: '1rem', 
        alignItems: 'center', 
        marginBottom: '1rem',
        flexWrap: 'wrap'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
          <label htmlFor="sortBy" style={{ fontWeight: 'bold' }}>Sort by:</label>
          <select
            id="sortBy"
            value={sortBy}
            onChange={e => setSortBy(e.target.value)}
            style={{
              padding: '0.5rem',
              borderRadius: '0.375rem',
              border: '1px solid #ccc',
              background: 'white'
            }}
          >
            <option value="score">Recommendation Score</option>
            <option value="stars">Stars</option>
            <option value="updated">Last Updated</option>
            <option value="name">Name (A-Z)</option>
          </select>
        </div>

        <button
          onClick={clearFilters}
          style={{
            padding: '0.5rem 1rem',
            backgroundColor: '#6b7280',
            color: 'white',
            border: 'none',
            borderRadius: '0.375rem',
            cursor: 'pointer'
          }}
        >
          Clear Filters
        </button>

        <span style={{ color: '#6b7280' }}>
          Showing {filteredProjects.length} of {allRecommendations.length} recommendations
        </span>
      </div>

      {/* Results Section */}
      {error && <div style={{ color: 'red', marginBottom: '1rem' }}>{error}</div>}

      <div className="results">
        {filteredProjects.length === 0 && allRecommendations.length > 0 && (
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            <p>No projects match your current filters.</p>
            <button
              onClick={clearFilters}
              style={{
                padding: '0.5rem 1rem',
                backgroundColor: '#22c55e',
                color: 'white',
                border: 'none',
                borderRadius: '0.375rem',
                cursor: 'pointer',
                marginTop: '0.5rem'
              }}
            >
              Clear Filters
            </button>
          </div>
        )}

        {allRecommendations.length === 0 && !loading && (
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            <p>No recommendations available at the moment.</p>
            <p style={{ color: '#6b7280', fontSize: '0.9rem' }}>
              Complete your profile or search for projects to get better recommendations.
            </p>
          </div>
        )}

        {filteredProjects.map(proj => (
          <div key={proj.id || proj.url} style={{ marginBottom: '1rem' }}>
            <ProjectCard 
              project={{
                ...proj,
                bookmark_id: proj.bookmark_id
              }} 
            />
            {/* Show recommendation score if available */}
            {proj.score && (
              <div style={{ 
                fontSize: '0.8rem', 
                color: '#6b7280', 
                marginTop: '0.25rem',
                fontStyle: 'italic'
              }}>
                Recommendation Score: {proj.score}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
