import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {
  Line, Doughnut, Bar,
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  BarElement,
  Tooltip,
  Legend
} from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

export default function ProjectProfilePage() {
  const { id } = useParams();  
  const [project, setProject] = useState(null);
  const [history, setHistory] = useState({ stars: [], issues: [] });

  useEffect(() => {
    // 1) fetch project details
    fetch(`/api/projects/${id}`)
      .then(r => r.json())
      .then(setProject);

    // 2) fetch metrics history (your backend needs to supply this)
    fetch(`/api/projects/${id}/metrics`)
      .then(r => r.json())
      .then(data => setHistory(data));
  }, [id]);

  if (!project) return <p>Loadingâ€¦</p>;

  // prepare chart data
  const dates = history.stars.map(p => p.date);
  const starData = history.stars.map(p => p.count);
  const issueData = history.issues.map(p => p.count);

  const langLabels = Object.keys(project.languageBreakdown);
  const langValues = Object.values(project.languageBreakdown);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>{project.name}</h1>
      <p>{project.description}</p>
      <a href={project.url} target="_blank" rel="noopener">View on GitHub</a>

      <div style={{ display: 'flex', gap: '2rem', marginTop: '2rem' }}>
        <div style={{ flex: 1 }}>
          <h3>Star Growth</h3>
          <Line
            data={{
              labels: dates,
              datasets: [{ label: 'Stars', data: starData, borderColor: 'blue', tension: 0.3 }]
            }}
          />
        </div>

        <div style={{ flex: 1 }}>
          <h3>Open Issues Over Time</h3>
          <Bar
            data={{
              labels: dates,
              datasets: [{ label: 'Open Issues', data: issueData, backgroundColor: 'orange' }]
            }}
          />
        </div>

        <div style={{ flex: 1 }}>
          <h3>Language Breakdown</h3>
          <Doughnut
            data={{
              labels: langLabels,
              datasets: [{ data: langValues, backgroundColor: ['#4bc0c0','#ff6384','#ffcd56','#36a2eb'] }]
            }}
          />
        </div>
      </div>
    </div>
  );
}
