import axios from 'axios';

const client = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000',
  timeout: 10000,
  withCredentials: false
});

const api = {
  // Projects
  getProjects(params) {
    return client.get('/api/projects', { params });
  },
  getProject(id) {
    return client.get(`/api/projects/${id}`);
  },

  // Bookmarks
  getBookmarks() {
    return client.get('/api/bookmarks');
  },
  addBookmark(projectId) {
    return client.post('/api/bookmarks', { project_id: projectId });
  },
  removeBookmark(bookmarkId) {
    return client.delete(`/api/bookmarks/${bookmarkId}`);
  },

  // Recommendations
  recommend(userProfile) {
    return client.post('/api/recommend', userProfile);
  },

  // GitHub sync
  syncGitHub() {
    return client.post('/api/github/sync');
  },

  // Auth
  login() {
    return client.post('/api/auth/login');
  },
  logout() {
    return client.post('/api/auth/logout');
  },
  getProfile() {
    return client.get('/api/auth/profile');
  }
};

export default api;
