import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import ProjectCard from '../components/ProjectCard';
import '../Styles/BookmarkPage.css';

export default function BookmarksPage() {
  const { userId, isAuthenticated, user } = useAuth();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  const [viewMode, setViewMode] = useState('grid'); // grid or list

  useEffect(() => {
    if (!isAuthenticated || !userId) return;

    setLoading(true);
    setError('');

    api.getBookmarks(userId)
      .then(res => {
        const bookmarks = Array.isArray(res.data) ? res.data : [];
        setProjects(bookmarks);
      })
      .catch(err => {
        console.error('Error fetching bookmarks:', err);
        setError('Failed to load bookmarks');
        setProjects([]);
      })
      .finally(() => setLoading(false));
  }, [userId, isAuthenticated]);

  // Filter and sort projects
  const filteredAndSortedProjects = React.useMemo(() => {
    let filtered = projects.filter(bookmark => {
      if (!searchQuery) return true;
      const project = bookmark.project;
      const query = searchQuery.toLowerCase();
      return (
        project.name.toLowerCase().includes(query) ||
        (project.description && project.description.toLowerCase().includes(query)) ||
        (project.languages && project.languages.some(lang => 
          lang.toLowerCase().includes(query)
        )) ||
        (project.topics && project.topics.some(topic => 
          topic.toLowerCase().includes(query)
        ))
      );
    });

    // Sort projects
    filtered.sort((a, b) => {
      const projectA = a.project;
      const projectB = b.project;

      switch (sortBy) {
        case 'name':
          return projectA.name.localeCompare(projectB.name);
        case 'stars':
          return (projectB.stars || 0) - (projectA.stars || 0);
        case 'updated':
          return new Date(projectB.last_updated || projectB.updated_at || 0) - 
                 new Date(projectA.last_updated || projectA.updated_at || 0);
        case 'oldest':
          return new Date(a.created_at || 0) - new Date(b.created_at || 0);
        case 'newest':
        default:
          return new Date(b.created_at || 0) - new Date(a.created_at || 0);
      }
    });

    return filtered;
  }, [projects, searchQuery, sortBy]);

  const handleRemoveBookmark = (projectId) => {
    setProjects(prev => prev.filter(bookmark => 
      bookmark.project_id !== projectId && bookmark.project.id !== projectId
    ));
  };

  if (!isAuthenticated) {
    return (
      <div className="bookmark-page">
        <div className="bookmark-empty-state">
          <div className="empty-state-icon">🔐</div>
          <h2>Authentication Required</h2>
          <p>Please log in to view your bookmarked projects.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bookmark-page">
      {/* Header Section */}
      <div className="bookmark-header">
        <div className="bookmark-title-section">
          <h1 className="bookmark-title">
            <span className="bookmark-icon">📚</span>
            Your Bookmarked Projects
          </h1>
          <p className="bookmark-subtitle">
            Welcome back, <strong>{user?.name || 'Developer'}</strong>! 
            Here are the projects you've saved for later.
          </p>
        </div>

        {/* Stats */}
        <div className="bookmark-stats">
          <div className="stat-card">
            <div className="stat-number">{projects.length}</div>
            <div className="stat-label">Total Bookmarks</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {new Set(projects.flatMap(b => b.project.languages || [])).size}
            </div>
            <div className="stat-label">Languages</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {projects.reduce((sum, b) => sum + (b.project.stars || 0), 0).toLocaleString()}
            </div>
            <div className="stat-label">Total Stars</div>
          </div>
        </div>
      </div>

      {/* Controls */}
      {projects.length > 0 && (
        <div className="bookmark-controls">
          <div className="bookmark-search">
            <div className="search-input-wrapper">
              <input
                type="text"
                placeholder="Search your bookmarks..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="bookmark-search-input"
              />
              <span className="search-icon">🔍</span>
            </div>
          </div>

          <div className="bookmark-filters">
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="bookmark-sort-select"
            >
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
              <option value="name">Name A-Z</option>
              <option value="stars">Most Stars</option>
              <option value="updated">Recently Updated</option>
            </select>

            <div className="view-mode-toggle">
              <button
                className={`view-mode-btn ${viewMode === 'grid' ? 'active' : ''}`}
                onClick={() => setViewMode('grid')}
                title="Grid View"
              >
                ⊞
              </button>
              <button
                className={`view-mode-btn ${viewMode === 'list' ? 'active' : ''}`}
                onClick={() => setViewMode('list')}
                title="List View"
              >
                ☰
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Content */}
      <div className="bookmark-content">
        {loading && (
          <div className="bookmark-loading">
            <div className="loading-spinner"></div>
            <p>Loading your bookmarks...</p>
          </div>
        )}

        {error && (
          <div className="bookmark-error">
            <div className="error-icon">⚠️</div>
            <h3>Oops! Something went wrong</h3>
            <p>{error}</p>
            <button 
              onClick={() => window.location.reload()} 
              className="retry-button"
            >
              Try Again
            </button>
          </div>
        )}

        {!loading && !error && projects.length === 0 && (
          <div className="bookmark-empty-state">
            <div className="empty-state-icon">📝</div>
            <h2>No Bookmarks Yet</h2>
            <p>Start exploring and bookmark some amazing projects!</p>
            <div className="empty-state-actions">
              <a href="/search" className="cta-button primary">
                🔍 Explore Projects
              </a>
              <a href="/recommend" className="cta-button secondary">
                ✨ Get Recommendations
              </a>
            </div>
          </div>
        )}

        {!loading && !error && filteredAndSortedProjects.length === 0 && projects.length > 0 && (
          <div className="bookmark-no-results">
            <div className="no-results-icon">🔍</div>
            <h3>No matching bookmarks</h3>
            <p>Try adjusting your search or filters.</p>
            <button 
              onClick={() => setSearchQuery('')} 
              className="clear-search-button"
            >
              Clear Search
            </button>
          </div>
        )}

        {!loading && !error && filteredAndSortedProjects.length > 0 && (
          <>
            <div className="results-info">
              <span className="results-count">
                Showing {filteredAndSortedProjects.length} of {projects.length} bookmarks
              </span>
              {searchQuery && (
                <span className="search-info">
                  for "<strong>{searchQuery}</strong>"
                </span>
              )}
            </div>

            <div className={`bookmark-grid ${viewMode}`}>
              {filteredAndSortedProjects.map((bookmark) => (
                <div key={bookmark.project_id || bookmark.id} className="bookmark-item">
                  <div className="bookmark-meta">
                    <span className="bookmark-date">
                      Saved {new Date(bookmark.created_at).toLocaleDateString()}
                    </span>
                  </div>

                  <div className="bookmark-project">
                    <ProjectCard 
                      project={{
                        ...bookmark.project,
                        bookmark_id: bookmark.id
                      }} 
                    />
                  </div>

                  <div className="bookmark-actions">
                    <button
                      onClick={() => handleRemoveBookmark(bookmark.project_id)}
                      className="remove-bookmark-btn"
                      title="Remove bookmark"
                    >
                      🗑️ Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
}
