import React, { useState } from 'react';
import axios from 'axios';
import '../Styles/SearchPage.css';

const SearchPage = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const search = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/projects`, {
        params: { q: query },
      });
      setResults(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="search-container">
      <h2 className="search-title">Search Projects</h2>
      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="e.g. kubernetes"
          className="search-input"
        />
        <button onClick={search} className="search-button">
          Search
        </button>
      </div>
      <ul>
        {results.map(proj => (
          <li key={proj.id} className="search-result">
            <a href={proj.url} target="_blank" rel="noopener noreferrer" className="search-result-link">
              {proj.name}
            </a>
            <p>{proj.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchPage;
