// src/context/AuthContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import { msalInstance, getToken } from '../services/auth';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [account, setAccount] = useState(null);
  const [idToken, setIdToken] = useState(null);
  const [initialized, setInitialized] = useState(false);

  useEffect(() => {
    async function init() {
      try {
        // Initialize MSAL instance if supported
        if (msalInstance.initialize) {
          await msalInstance.initialize();
        }
        const accts = msalInstance.getAllAccounts();
        if (accts.length > 0) {
          setAccount(accts[0]);
          const token = await getToken();
          setIdToken(token);
        }
      } catch (err) {
        console.error('MSAL initialization error:', err);
      } finally {
        setInitialized(true);
      }
    }
    init();
  }, []);

  const login = async () => {
    if (!initialized) return;
    try {
      const loginResult = await msalInstance.loginPopup({
        scopes: ['openid', 'profile', 'email'],
      });
      setAccount(loginResult.account);
      const token = await getToken();
      setIdToken(token);
    } catch (e) {
      console.error('Login error:', e);
    }
  };

  const logout = () => {
    msalInstance.logoutPopup();
    setAccount(null);
    setIdToken(null);
  };

  if (!initialized) {
    return null; // or a loading indicator
  }

  return (
    <AuthContext.Provider value={{ account, idToken, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
