import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import ProjectCard from '../components/ProjectCard';

export default function BookmarksPage() {
  const { userId, isAuthenticated } = useAuth();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!isAuthenticated || !userId) return;

    setLoading(true);
    setError('');

    api.getBookmarks(userId)
      .then(res => {
        const bookmarks = Array.isArray(res.data) ? res.data : [];
        setProjects(bookmarks);
      })
      .catch(err => {
        console.error('Error fetching bookmarks:', err);
        setError('Failed to load bookmarks');
        setProjects([]);
      })
      .finally(() => setLoading(false));
  }, [userId, isAuthenticated]);

  if (!isAuthenticated) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>Please log in to view your bookmarks.</h2>
      </div>
    );
  }

  if (loading) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>Loading bookmarks...</h2>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Your Bookmarked Projects</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {projects.length === 0 ? (
        <p>No bookmarks yet. Start exploring and bookmark some projects!</p>
      ) : (
        <div>
          {projects.map(bookmark => (
            <ProjectCard 
              key={bookmark.project_id || bookmark.id} 
              project={{
                ...bookmark.project,
                bookmark_id: bookmark.id // Mark as bookmarked
              }} 
            />
          ))}
        </div>
      )}
    </div>
  );
}
