import React, { useState, useEffect } from 'react';
import api from '../services/api';

export default function ProjectCard({ project }) {
  const [bookmarked, setBookmarked] = useState(false);
  const [bmId, setBmId] = useState(null);

  useEffect(() => {
    if (project.bookmark_id) {
      setBookmarked(true);
      setBmId(project.bookmark_id);
    }
  }, [project]);

  const toggle = async () => {
    if (!bookmarked) {
      const res = await api.post('/api/bookmarks', { project_id: project.id });
      setBookmarked(true);
      setBmId(res.data.id);
    } else {
      await api.delete(`/api/bookmarks/${bmId}`);
      setBookmarked(false);
      setBmId(null);
    }
  };

  return (
    <div className="project-card">
      <h3>{project.name}</h3>
      <p>{project.description}</p>
      <button onClick={toggle}>
        {bookmarked ? 'üíî Unbookmark' : '‚≠ê Bookmark'}
      </button>
    </div>
  );
}
