// src/pages/ProfilePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const ProfilePage = () => {
  const { account, idToken } = useAuth();
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const fetchGitHubProfile = async () => {
      const token = await idToken?.();
      if (!token) return;

      try {
    const res = await axios.get('https://api.github.com/user', {
      headers: { Authorization: `Bearer ${token}` },
    });
    setProfile(res.data);
  } catch (err) {
    console.error('Failed to fetch GitHub profile:', err);
  }
};

    if (account) fetchGitHubProfile();
  }, [account, idToken]);

  if (!profile) return <p>Loading...</p>;

  return (
    <div className="p-8">
      <h2 className="text-2xl mb-4">GitHub Profile</h2>
      <img src={profile.avatar_url} alt="Avatar" width="100" />
      <p><strong>Username:</strong> {profile.login}</p>
      <p><strong>Name:</strong> {profile.name}</p>
      <p><strong>Email:</strong> {profile.email}</p>
      <p><strong>Location:</strong> {profile.location}</p>
      <p><strong>Bio:</strong> {profile.bio}</p>
      <a href={profile.html_url} target="_blank" rel="noreferrer">View GitHub</a>
    </div>
  );
};

export default ProfilePage;
