import React from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "../Styles/Navbar.css";

const Navbar = () => {
  const { account, logout } = useAuth();
  const [showProfile, setShowProfile] = useState(false);

  const handleToggleProfile = () => {
    setShowProfile(prev => !prev);
  };

  return (
    <>
      <div className="navbar">
        <div>
          <Link to="/" className="navbar-link">Home</Link>
          <Link to="/search" className="navbar-link">Search</Link>
          <Link to="/profile" className="navbar-link">Profile</Link>
          <Link to="/bookmarks" className="navbar-link">Bookmarks</Link>
        </div>
        {account && (
          <div className="navbar-right">
            <button className="navbar-button" onClick={handleToggleProfile}>
              Hi, {account.name?.toUpperCase()}
            </button>
            <button className="navbar-button" onClick={logout}>Logout</button>
          </div>
        )}
      </div>

      {/* Profile Dropdown */}
      {showProfile && account && (
        <div className="profile-dropdown">
          {account.picture && (
            <img
              src={account.picture}
              alt="Profile"
              className="profile-avatar-dropdown"
            />
          )}
          <p><strong>Name:</strong> {account.name}</p>
          <p><strong>Email:</strong> {account.email}</p>
          <p><strong>Provider:</strong> {account.sub?.split('|')[0]}</p>
        </div>
      )}
    </>
  );
};

export default Navbar;
